import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Sidebar from "./Sidebar";
import { faHome, faCog } from "@fortawesome/free-solid-svg-icons";
console.log("Access Token:", cookies.access_token);

export default function Navbar() {
    const [cookies, setCookies, removeCookie] = useCookies(["access_token"]);
    const navigate = useNavigate();
    const location = useLocation();
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        setIsLoggedIn(!!cookies.access_token); // Update state if token exists
        console.log("Updated isLoggedIn:", !!cookies.access_token);
    }, [cookies]);

    const logout = () => {
        removeCookie("access_token"); // Remove token from cookies
        window.localStorage.clear();
        setIsLoggedIn(false);
        navigate("/login"); // Redirect to login page
    };

    const links = [
        { name: "Home", path: "/", icon: faHome },
        { name: "AddRecipe", path: "/add-recipe" },
        { name: "Settings", path: "/settings", icon: faCog },
    ];

    if (!isLoggedIn) {
        links.push({ name: "Login", path: "/login" });
        links.push({ name: "Register", path: "/register" });
    }

    return (
        <>
            <div className="navbar container">
                <Link to="/" className="logo">F<span>oo</span>dokii</Link>

                <div className="nav-links">
                    {links.map(link => (
                        <Link key={link.name} to={link.path} className={location.pathname === link.path ? "active" : ""}>
                            {link.name}
                        </Link>
                    ))}

                    {/* Show Logout button only if logged in */}
                    {isLoggedIn && (
                        <button 
                            onClick={logout} 
                            style={{
                                marginLeft: "20px",
                                padding: "8px 15px",
                                backgroundColor: "#ff4d4d",
                                color: "white",
                                border: "none",
                                borderRadius: "5px",
                                cursor: "pointer",
                                fontSize: "16px"
                            }}
                        >
                            Logout
                        </button>
                    )}
                </div>
            </div>
        </>
    );
}
