import React, { useEffect, useState } from "react";
const handleSubmit = async (e) => {
    e.preventDefault();
    const userId = localStorage.getItem("userId"); // Get logged-in user ID

    if (!userId) {
        alert("You must be logged in to add a recipe");
        return;
    }

    const response = await fetch("http://localhost:3007/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...recipe, userId }), // Attach userId
    });

    const data = await response.json();

    if (response.ok) {
        alert("Recipe added successfully");
        setRecipes([...recipes, data]); // Update state to reflect new recipe
    } else {
        alert("Error adding recipe");
    }
};


const Recipes = () => {
    const [recipes, setRecipes] = useState([]);
    const userId = localStorage.getItem("userId"); // Get logged-in user's ID

    useEffect(() => {
        if (!userId) {
            console.error("User not logged in");
            return;
        }

        fetch(`http://localhost:3007/recipes/${userId}`) // Fetch only this user's recipes
            .then((res) => res.json())
            .then((data) => setRecipes(data))
            .catch((err) => console.error("Error fetching recipes:", err));
    }, [userId]);

    return (
        <div>
            <h2>Your Recipes</h2>
            {recipes.length === 0 ? (
                <p>No recipes added yet.</p>
            ) : (
                <ul>
                    {recipes.map((recipe) => (
                        <li key={recipe._id}>
                            <h3>{recipe.Recipe_title}</h3>
                            <p>Ingredients: {recipe.Ingredients}</p>
                            <p>Instructions: {recipe.Instructions}</p>
                            <p>Cooking Time: {recipe.Cooking_time} mins</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Recipes;
