import { Link, useLocation, useNavigate } from "react-router-dom";
import { useState } from "react";
import Sidebar from "./Sidebar";
import React from "react";
import { faHome, faCog } from "@fortawesome/free-solid-svg-icons";
import { useCookies } from "react-cookie";

export default function Navbar() {
    const [cookies, setCookies] = useCookies(["access_token"]);
    const navigate = useNavigate();
    const [showSidebar, setShowSidebar] = useState(false);
    const location = useLocation();

    const logout = () => {
        setCookies("access_token", ""); // Remove token
        window.localStorage.clear();
        navigate("/login"); // Redirect to login page
    };

    const links = [
        { name: "Home", path: "/", icon: faHome },
        { name: "AddRecipe", path: "/add-recipe" },
        { name: "Settings", path: "/settings", icon: faCog },
    ];

    // Show login/register only if user is NOT logged in
    if (!cookies.access_token) {
        links.push({ name: "Login", path: "/login", icon: faCog });
        links.push({ name: "Register", path: "/register", icon: faCog });
    }

    function closeSidebar() {
        setShowSidebar(false);
    }

    return (
        <>
            <div className="navbar container">
                <Link to="/" className="logo">F<span>oo</span>dokii</Link>

                <div className="nav-links">
                    {links.map(link => (
                        <Link className={location.pathname === link.path ? "active" : ""} to={link.path} key={link.name}>{link.name}</Link>
                    ))}

                    {/* Show Logout button only if user is logged in */}
                    {cookies.access_token && (
                        <button 
                            onClick={logout} 
                            style={{
                                marginLeft: "20px",
                                padding: "8px 15px",
                                backgroundColor: "#ff4d4d",
                                color: "white",
                                border: "none",
                                borderRadius: "5px",
                                cursor: "pointer",
                                fontSize: "16px"
                            }}
                        >
                            Logout
                        </button>
                    )}
                </div>

                <div onClick={() => setShowSidebar(true)} className={showSidebar ? "sidebar-btn active" : "sidebar-btn"}>
                    <div className="bar"></div>
                    <div className="bar"></div>
                    <div className="bar"></div>
                </div>
            </div>

            {showSidebar && <Sidebar close={closeSidebar} links={links} />}
        </>
    );
}
