import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddRecipe = () => {
  const [recipe, setRecipe] = useState({
    Recipe_title: '',
    Ingredients: [''],
    Instructions: '',
    Cooking_time: '',
    Difficulty: 'easy'
  });

  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Ensure all fields are properly formatted
    if (!recipe.title || !recipe.ingredients.length || !recipe.instructions || !recipe.cookingTime) {
      alert("All fields are required!");
      return;
    }
  
    const newRecipe = {
      Recipe_title: recipe.title.trim(),
      Ingredients: recipe.ingredients.map(ing => ing.trim()), // Ensure ingredients are valid
      Instructions: recipe.instructions.trim(),
      Cooking_time: Number(recipe.cookingTime) // Ensure cooking time is a number
    };
  
    try {
      const response = await fetch('http://localhost:3007/api/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newRecipe)
      });
  
      const data = await response.json();
  
      if (response.ok) {
        alert('Recipe added successfully!');
        navigate('/'); // Redirect after success
      } else {
        throw new Error(data.message || 'Failed to add recipe');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Server error. Please try again.');
    }
  };
  

  const handleChange = (e) => {
    setRecipe({ ...recipe, [e.target.name]: e.target.value });
  };

  const handleIngredientChange = (index, value) => {
    const newIngredients = [...recipe.Ingredients];
    newIngredients[index] = value;
    setRecipe({ ...recipe, Ingredients: newIngredients });
  };

  const addIngredient = () => {
    setRecipe({ ...recipe, Ingredients: [...recipe.Ingredients, ''] });
  };

  return (
    <div className="form-container">
      <h2>Add New Recipe</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Recipe Title:</label>
          <input
            type="text"
            name="Recipe_title"
            value={recipe.Recipe_title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Ingredients:</label>
          {recipe.Ingredients.map((ingredient, index) => (
            <div key={index} className="ingredient-input">
              <input
                type="text"
                value={ingredient}
                onChange={(e) => handleIngredientChange(index, e.target.value)}
                required
              />
            </div>
          ))}
          <button type="button" onClick={addIngredient} className="add-button">
            Add Ingredient
          </button>
        </div>

        <div className="form-group">
          <label>Instructions:</label>
          <textarea
            name="Instructions"
            value={recipe.Instructions}
            onChange={handleChange}
            required
            rows="5"
          />
        </div>

        <div className="form-group">
          <label>Cooking Time (minutes):</label>
          <input
            type="number"
            name="Cooking_time"
            value={recipe.Cooking_time}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Difficulty:</label>
          <select name="Difficulty" value={recipe.Difficulty} onChange={handleChange}>
            <option value="easy">Easy</option>
            <option value="medium">Medium</option>
            <option value="hard">Hard</option>
          </select>
        </div>

        <button type="submit" className="submit-btn">Submit Recipe</button>
      </form>
    </div>
  );
};

export default AddRecipe;
