import { Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import { faHome, faList, faCog, faSignOutAlt } from "@fortawesome/free-solid-svg-icons";

export default function Navbar() {
    const [showSidebar, setShowSidebar] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(false); // Track authentication state
    const location = useLocation();
    
    useEffect(() => {
        // Check localStorage for authentication status
        const authStatus = localStorage.getItem("isAuthenticated") === "true";
        setIsAuthenticated(authStatus);
    }, []);

    const links = [
        {
            name: "Home",
            path: "/",
            icon: faHome
        },
        {
            name: "AddRecipe",
            path: "/add-recipe",
        },
        {
            name: "Recipes",
            path: "/recipes",
            icon: faList
        },
        {
            name: "Settings",
            path: "/settings",
            icon: faCog
        }
    ];
    
    if (isAuthenticated) {
        links.push({
            name: "Logout",
            path: "#",
            icon: faSignOutAlt,
            action: () => handleLogout()
        });
    } else {
        links.push(
            { name: "Login", path: "/login", icon: faCog },
            { name: "Register", path: "/register", icon: faCog }
        );
    }

    function closeSidebar() {
        setShowSidebar(false);
    }
    
    function handleLogout() {
        // Perform logout logic
        localStorage.removeItem("isAuthenticated"); // Clear stored authentication status
        setIsAuthenticated(false);
        console.log("User logged out");
    }
    
    return (
        <>
            <div className="navbar container">
                <Link to="/" className="logo">F<span>oo</span>dokii</Link>
                <div className="nav-links">
                    {links.map(link => (
                        link.path !== "#" ? (
                            <Link className={location.pathname === link.path ? "active" : ""} to={link.path} key={link.name}>{link.name}</Link>
                        ) : (
                            <span className="logout" onClick={link.action} key={link.name}>{link.name}</span>
                        )
                    ))}
                </div>
                <div onClick={() => setShowSidebar(true)} className={showSidebar ? "sidebar-btn active" : "sidebar-btn"}>
                    <div className="bar"></div>
                    <div className="bar"></div>
                    <div className="bar"></div>
                </div>
            </div>
            
            {showSidebar && <Sidebar close={closeSidebar} links={links} />}
        </>
    );
}
