import { Link, useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import { faHome, faList, faCog } from "@fortawesome/free-solid-svg-icons";

export default function Navbar() {
    const [showSidebar, setShowSidebar] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("user"));
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        // Listen for login changes across the app
        const updateLoginStatus = () => {
            setIsLoggedIn(!!localStorage.getItem("user"));
        };

        window.addEventListener("storage", updateLoginStatus);

        return () => {
            window.removeEventListener("storage", updateLoginStatus);
        };
    }, []);

    function handleLogin() {
        localStorage.setItem("user", "true");  // Mock login (store token in real case)
        setIsLoggedIn(true); // ðŸ”¹ Updates state instantly
    }

    function handleLogout() {
        localStorage.removeItem("user"); // Remove user data
        setIsLoggedIn(false); // ðŸ”¹ Updates state instantly
        navigate("/login");
    }

    const links = [
        { name: "Home", path: "/", icon: faHome },
        { name: "AddRecipe", path: "/add-recipe" },
        { name: "Recipes", path: "/recipes", icon: faList },
        { name: "Settings", path: "/settings", icon: faCog },
    ];

    return (
        <>
            <div className="navbar container">
                <Link to="/" className="logo">F<span>oo</span>dokii</Link>
                <div className="nav-links">
                    {links.map(link => (
                        <Link className={location.pathname === link.path ? "active" : ""} to={link.path} key={link.name}>{link.name}</Link>
                    ))}
                    {isLoggedIn ? (
                        <button onClick={handleLogout} className="logout-btn">Logout</button>
                    ) : (
                        <>
                            <button onClick={handleLogin} className="login-btn">Login</button>
                            <Link to="/register" className={location.pathname === "/register" ? "active" : ""}>Register</Link>
                        </>
                    )}
                </div>
                <div onClick={() => setShowSidebar(true)} className={showSidebar ? "sidebar-btn active" : "sidebar-btn"}>
                    <div className="bar"></div>
                    <div className="bar"></div>
                    <div className="bar"></div>
                </div>
            </div>

            {showSidebar && <Sidebar close={() => setShowSidebar(false)} links={links} />}
        </>
    );
}
