import { Link, useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import { faHome, faList, faCog, faSignOutAlt, faSignInAlt, faUserPlus } from "@fortawesome/free-solid-svg-icons";

export default function Navbar() {
  const [showSidebar, setShowSidebar] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is authenticated (Token exists in localStorage)
    setIsAuthenticated(!!localStorage.getItem("token"));
  }, [location]);

  function handleLogout() {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    alert("Logged out successfully!");
    navigate("/login");
  }

  const links = [
    { name: "Home", path: "/", icon: faHome },
    { name: "Add Recipe", path: "/add-recipe", icon: faList },
    { name: "Recipes", path: "/recipes", icon: faList },
    { name: "Settings", path: "/settings", icon: faCog },
  ];

  return (
    <>
      <div className="navbar container">
        <Link to="/" className="logo">
          F<span>oo</span>dokii
        </Link>

        <div className="nav-links">
          {links.map((link) => (
            <Link className={location.pathname === link.path ? "active" : ""} to={link.path} key={link.name}>
              {link.name}
            </Link>
          ))}

          {/* Show Login/Register if not authenticated, else show Logout */}
          {!isAuthenticated ? (
            <>
              <Link to="/login">
                <i className="icon">{faSignInAlt.iconName}</i> Login
              </Link>
              <Link to="/register">
                <i className="icon">{faUserPlus.iconName}</i> Register
              </Link>
            </>
          ) : (
            <button onClick={handleLogout} className="logout-btn">
              <i className="icon">{faSignOutAlt.iconName}</i> Logout
            </button>
          )}
        </div>

        <div onClick={() => setShowSidebar(true)} className={showSidebar ? "sidebar-btn active" : "sidebar-btn"}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>
      </div>

      {showSidebar && <Sidebar close={() => setShowSidebar(false)} links={links} />}
    </>
  );
}
