import React, { useState } from "react";
import { addRecipe } from "./api";

const AddRecipe = () => {
  const [recipe, setRecipe] = useState({ title: "", ingredients: "", instructions: "" });
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!token) {
      setError("You must be logged in to add a recipe.");
      return;
    }

    try {
      const response = await addRecipe(recipe, token);
      if (response.success) {
        alert("Recipe added successfully!");
        setRecipe({ title: "", ingredients: "", instructions: "" });
      } else {
        setError(response.message || "Failed to add recipe.");
      }
    } catch (err) {
      setError("Server error. Please try again.");
    }
  };

  return (
    <div className="add-recipe-container">
      <h2>Add Recipe</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Recipe Title" onChange={(e) => setRecipe({ ...recipe, title: e.target.value })} required />
        <textarea name="ingredients" placeholder="Ingredients" onChange={(e) => setRecipe({ ...recipe, ingredients: e.target.value })} required />
        <textarea name="instructions" placeholder="Instructions" onChange={(e) => setRecipe({ ...recipe, instructions: e.target.value })} required />
        <button type="submit">Add Recipe</button>
      </form>
    </div>
  );
};

export default AddRecipe;
