import React, { useState } from "react";

const AddRecipe = () => {
    // Define state variables for form inputs
    const [recipeTitle, setRecipeTitle] = useState("");
    const [ingredients, setIngredients] = useState("");
    const [instructions, setInstructions] = useState("");
    const [cookingTime, setCookingTime] = useState("");

    const handleSubmit = async (e) => {
      e.preventDefault();  // Prevents page reload
  
      const newRecipe = {
          title: recipeTitle,         // Ensure this matches the backend field names
          ingredients: ingredients,   // Should be an array or string
          instructions: instructions,
          cookingTime: cookingTime,   // Ensure this is a number, if required
      };
  
      console.log("Sending data:", newRecipe);  // Debugging log
  
      try {
          const response = await fetch("http://localhost:3007/add", {  // Ensure this URL is correct
              method: "POST",
              headers: {
                  "Content-Type": "application/json", // ✅ Ensures JSON format
                  

              },
              body: JSON.stringify(newRecipe), // Convert JS object to JSON
          });
  
          if (!response.ok) {
              throw new Error("Failed to add recipe");
          }
  
          const data = await response.json();
          console.log("Recipe added successfully:", data);
      } catch (error) {
          console.error("Error:", error);
      }
      
  };
  

    return (
        <div>
            <h2>Add Recipe</h2>
            <form onSubmit={handleSubmit}>
                <label>Recipe Title:</label>
                <input
                    type="text"
                    value={recipeTitle}
                    onChange={(e) => setRecipeTitle(e.target.value)}
                />

                <label>Ingredients:</label>
                <textarea
                    value={ingredients}
                    onChange={(e) => setIngredients(e.target.value)}
                />

                <label>Instructions:</label>
                <textarea
                    value={instructions}
                    onChange={(e) => setInstructions(e.target.value)}
                />

                <label>Cooking Time (mins):</label>
                <input
                    type="number"
                    value={cookingTime}
                    onChange={(e) => setCookingTime(e.target.value)}
                />

                <button type="submit">Add Recipe</button>
            </form>
        </div>
    );
};

export default AddRecipe;
