import { Link, useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHome, faList, faCog, faSignOutAlt, faSignInAlt, faUserPlus } from "@fortawesome/free-solid-svg-icons";

export default function Navbar() {
    const [showSidebar, setShowSidebar] = useState(false);
    const [user, setUser] = useState(null);
    const location = useLocation();
    const navigate = useNavigate();

    // Check login state on page load
    useEffect(() => {
        const loggedInUser = localStorage.getItem("user");
        if (loggedInUser) {
            setUser(JSON.parse(loggedInUser));
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("user"); // Clear user session
        setUser(null); // Update state
        navigate("/login"); // Redirect to login
    };

    const links = [
        { name: "Home", path: "/", icon: faHome },
        { name: "Add Recipe", path: "/add-recipe" },
        { name: "Recipes", path: "/recipes", icon: faList },
        { name: "Settings", path: "/settings", icon: faCog }
    ];

    function closeSidebar() {
        setShowSidebar(false);
    }

    return (
        <>
            <div className="navbar container">
                <Link to="/" className="logo">F<span>oo</span>dokii</Link>
                <div className="nav-links">
                    {links.map(link => (
                        <Link className={location.pathname === link.path ? "active" : ""} to={link.path} key={link.name}>
                            <FontAwesomeIcon icon={link.icon} /> {link.name}
                        </Link>
                    ))}

                    {user ? (
                        <button className="logout-btn" onClick={handleLogout}>
                            <FontAwesomeIcon icon={faSignOutAlt} /> Logout
                        </button>
                    ) : (
                        <>
                            <Link to="/login"><FontAwesomeIcon icon={faSignInAlt} /> Login</Link>
                            <Link to="/register"><FontAwesomeIcon icon={faUserPlus} /> Register</Link>
                        </>
                    )}
                </div>
                <div onClick={() => setShowSidebar(true)} className={showSidebar ? "sidebar-btn active" : "sidebar-btn"}>
                    <div className="bar"></div>
                    <div className="bar"></div>
                    <div className="bar"></div>
                </div>
            </div>
            
            {showSidebar && <Sidebar close={closeSidebar} links={links} />}
        </>
    );
}
