import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API_BASE_URL from './config';

const AddRecipe = () => {
  const [recipe, setRecipe] = useState({
    title: '',
    ingredients: [''],
    instructions: '',
    cookingTime: '',
    difficulty: 'easy'
  });
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token"); // Get authentication token

    try {
      const response = await fetch(`${API_BASE_URL}/add`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}` // Send token for authentication
        },
        body: JSON.stringify(recipe),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        navigate('/'); // Redirect to home after adding a recipe
      } else {
        alert(data.message || 'Failed to add recipe');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Something went wrong!');
    }
  };

  const handleChange = (e) => {
    setRecipe({
      ...recipe,
      [e.target.name]: e.target.value
    });
  };

  const handleIngredientChange = (index, value) => {
    const newIngredients = [...recipe.ingredients];
    newIngredients[index] = value;
    setRecipe({ ...recipe, ingredients: newIngredients });
  };

  const addIngredient = () => {
    setRecipe({ 
      ...recipe, 
      ingredients: [...recipe.ingredients, ''] 
    });
  };

  return (
    <div className="form-container">
      <h2>Add New Recipe</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="title" value={recipe.title} onChange={handleChange} required />
        {recipe.ingredients.map((ingredient, index) => (
          <input key={index} type="text" value={ingredient} onChange={(e) => handleIngredientChange(index, e.target.value)} required />
        ))}
        <button type="button" onClick={addIngredient}>Add Ingredient</button>
        <textarea name="instructions" value={recipe.instructions} onChange={handleChange} required />
        <input type="number" name="cookingTime" value={recipe.cookingTime} onChange={handleChange} required />
        <select name="difficulty" value={recipe.difficulty} onChange={handleChange}>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
        <button type="submit">Submit Recipe</button>
      </form>
    </div>
  );
};

export default AddRecipe;
